<script>
import Container from '@/components/global/Container.vue'

export default {
	name: 'Sidebar',
	components: {
		Container
	},
	props: {
		collapsed: Boolean
	},
	data() {
		return {
			init: this.mapProps(),
			current: this.mapProps(),
			width: {
				base: '212px',
				smallest: 'auto'
			}
		}
	},
	computed: {
		currentWidth() {
			return this.current.collapsed ? this.width.smallest : this.width.base
		}
	},
	methods: {
		mapProps() {
			return {
				collapsed: this.collapsed
			}
		}
	}
}
